import { Link } from "react-router-dom";
import { useState, useContext, useEffect } from "react";
import { AuthContext } from "../../context/AuthContext";
import axios from "axios";
import useFetch from "../../hooks/useFetch";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUserPen } from "@fortawesome/free-solid-svg-icons";
import "./profile.css";

const ProfileImage = ({ src, alt, onClick }) => {
    return (
        <>
            {src ? (
                <img
                    src={src}
                    alt={alt}
                    className="profileimg"
                    onClick={onClick}
                />
            ) : (
                <div
                    style={{
                        display: "flex",
                        justifyContent: "center",
                        alignItems: "center",
                        width: "150px", // 이미지 크기에 맞춰 조정
                        height: "150px", // 이미지 크기에 맞춰 조정
                        backgroundColor: "var(--c-black)", // 원하는 배경 색상
                        borderRadius: "var(--b-large)", // 이미지 모서리 스타일
                    }}
                    onClick={onClick}
                >
                    <FontAwesomeIcon
                        icon={faUserPen}
                        style={{
                            color: "var(--c-white)",
                            fontSize: "var(--s-huge)",
                            border: "1px solid var(--c-white)",
                            borderRadius: "50%",
                        }}
                    />
                </div>
            )}
        </>
    );
};

const Profile = () => {
    const { user } = useContext(AuthContext);
    const apiUrl = process.env.REACT_APP_API_URL;

    const {
        data: userData,
        loading,
        error,
    } = useFetch(`${apiUrl}/users/${user._id}`);

    const [credentials, setCredentials] = useState({
        phone: "",
        nickname: "",
        img: "",
        bio: "",
        username: "",
    });

    const [imageFile, setImageFile] = useState(null);

    useEffect(() => {
        if (userData) {
            setCredentials({
                phone: userData.phone || "",
                nickname: userData.nickname || "",
                img: userData.img || "",
                bio: userData.bio || "",
                username: userData.username || "",
            });
        }
    }, [userData]);

    const handleChange = (e) => {
        setCredentials((prev) => ({ ...prev, [e.target.id]: e.target.value }));
    };

    const handleImageChange = (e) => {
        const file = e.target.files[0];
        setImageFile(file);
        const reader = new FileReader();
        reader.onloadend = () => {
            setCredentials((prev) => ({ ...prev, img: reader.result }));
        };
        reader.readAsDataURL(file);
    };

    const handleClick = async (e) => {
        e.preventDefault();
        try {
            const formData = new FormData();
            formData.append("phone", credentials.phone);
            formData.append("nickname", credentials.nickname);
            formData.append("img", credentials.img);
            formData.append("bio", credentials.bio);
            if (imageFile) {
                formData.append("profile", imageFile);
            }
            const res = await axios.put(
                `${apiUrl}/users/${user._id}`,
                formData,
                {
                    withCredentials: true,
                    headers: {
                        "Content-Type": "multipart/form-data",
                    },
                }
            );
            const newImgUrl = `${apiUrl}/${
                res.data.img
            }?${new Date().getTime()}`;
            setCredentials((prev) => ({ ...prev, img: newImgUrl }));
            window.location.href = "/profile";
        } catch (err) {
            console.log(err);
        }
    };

    if (loading) {
        return <div>Loading...</div>;
    }
    if (error) {
        return <div>Error: {error.message}</div>;
    }

    return (
        <div className="profilewrapper">
            <form className="profileForm">
                <div className="pageTitle">MY PAGE</div>
                {/* <img
                    src={
                        credentials.profileimg || (
                            <FontAwesomeIcon
                                icon={faUserPen}
                                style={{
                                    color: "var(--c-dark-gray)",
                                    fontSize: "var(--s-medium)",
                                }}
                            />
                        )
                    }
                    alt="profileimg"
                    className="profileimg"
                    onClick={() =>
                        document.getElementById("imageInput").click()
                    }
                /> */}
                <ProfileImage />
                <input
                    type="file"
                    id="imageInput"
                    style={{ display: "none" }}
                    onChange={handleImageChange}
                />
                <label htmlFor="username">아이디</label>
                <input
                    type="text"
                    id="username"
                    autoComplete="off"
                    value={credentials.username}
                    readOnly
                />
                <span className="notChangeable">
                    {" "}
                    아이디는 변경이 불가합니다.{" "}
                </span>
                <label htmlFor="nickname">닉네임</label>
                <input
                    type="text"
                    id="nickname"
                    autoComplete="off"
                    onChange={handleChange}
                    value={credentials.nickname}
                />
                <label htmlFor="phone">전화번호</label>
                <input
                    type="text"
                    id="phone"
                    autoComplete="off"
                    onChange={handleChange}
                    value={credentials.phone}
                />
                <label htmlFor="bio">소개</label>
                <input
                    type="text"
                    id="bio"
                    autoComplete="off"
                    onChange={handleChange}
                    value={credentials.bio}
                />
                <button onClick={handleClick}>수정</button>
                <Link to="/">
                    <button>확인</button>
                </Link>
            </form>
        </div>
    );
};

export default Profile;
