import React from "react";
import useFetch from "../../hooks/useFetch";
import "./musiclist.css";

const MusicList = () => {
    const aptUrl = process.env.REACT_APP_API_URL;
    const { data, loading, error } = useFetch(`${aptUrl}/musics?featured=true&limit=4`);
  
    return (
        <div className="mp">
        {loading ? ("loading") : (
          <>
            {data.map((item) => (
              <div className="mpItem" key={item._id}>
                <img src={item.photos[0]} alt="" className="mpImg"/>
                <span className="mpName">{item.name}</span>
                <span className="mpCity">{item.city}</span>
                <span className="mpPrice">Starting from ${item.cheapestPrice}</span>
                {item.rating && <div className="mpRating">
                  <button>{item.rating}</button>
                  <span>Excellent</span>
                  </div>}
              </div>
            ))}
          </>
        )}
      </div>
    );
};

export default MusicList;
