import axios from "axios";
import Nav from "../../components/nav/Nav.jsx";
import Footer from "../../components/footer/Footer.jsx";
import React, { useState } from "react";
import "./upload.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowLeft } from "@fortawesome/free-solid-svg-icons";

const Upload = () => {
    const [credentials, setCredentials] = useState({
        title: "",
        fileurl: "",
        thumbimgUrl: "",
        desc: "",
    });

    const [file, setFile] = useState(null);
    const [thumbFile, setThumbFile] = useState(null);

    const handleChange = (e) => {
        setCredentials((prev) => ({ ...prev, [e.target.id]: e.target.value }));
    };

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        setFile(file);
    };

    const handleThumbFileChange = (e) => {
        const file = e.target.files[0];
        setThumbFile(file);
    };

    const handleClick = async (e) => {
        e.preventDefault();
        try {
            const apiUrl = process.env.REACT_APP_API_URL;
            const formData = new FormData();
            formData.append("title", credentials.title);
            formData.append("desc", credentials.desc);
            if (file) {
                formData.append("fileurl", file);
            }
            if (thumbFile) {
                formData.append("thumbimgUrl", thumbFile);
            }

            const res = await axios.post(`${apiUrl}/musics`, formData, {
                headers: {
                    "Content-Type": "multipart/form-data",
                },
            });

            console.log("res data : ", res.data);
        } catch (err) {
            console.log("res err : ", err);
        }
    };

    return (
        <div>
            <Nav />
            <div className="uploadForm">
                <a className="backBtn" href="/">
                    <FontAwesomeIcon
                        icon={faArrowLeft}
                        style={{
                            color: "var(--c-white)",
                            fontSize: "var(--s-large)",
                        }}
                    />
                </a>
                <div className="uploadWrapper">
                    <div className="pageTitle">UPLOAD</div>
                    <form>
                        <div className="ImgeWrapper">
                            <img
                                src={credentials.thumbimgUrl}
                                alt="thumbimgUrl"
                                className="thumbimgUrl"
                                onClick={() =>
                                    document
                                        .getElementById("imageInput")
                                        .click()
                                }
                            />
                        </div>
                        <input
                            type="file"
                            id="thumbimgUrl"
                            style={{ display: "none" }}
                            onChange={handleImageChange}
                        />

                        <label htmlFor="title">Title *</label>
                        <input
                            type="text"
                            id="title"
                            className="uploadInput"
                            autoComplete="off"
                            onChange={handleChange}
                            value={credentials.title}
                        />
                        <label htmlFor="fileurl">첨부파일 *</label>
                        <input
                            type="file"
                            id="fileurl"
                            className="uploadInput"
                            autoComplete="off"
                            onChange={handleFileChange}
                        />
                        <label htmlFor="desc">설명 *</label>
                        <input
                            type="text"
                            id="desc"
                            className="uploadInput"
                            autoComplete="off"
                            onChange={handleChange}
                            value={credentials.desc}
                        />
                        <button
                            type="submit"
                            className="uploadBtn1"
                            onClick={handleClick}
                        >
                            업로드
                        </button>
                    </form>
                </div>
            </div>
            <Footer />
        </div>
    );
};

export default Upload;
