import axios from "axios";
import React, { useContext } from "react";
import { AuthContext } from "../../context/AuthContext";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faMagnifyingGlass } from "@fortawesome/free-solid-svg-icons";
import { useNavigate } from "react-router-dom"
import "./hsearch.css";

const HSearch = () => {
    const navigate = useNavigate();
    const { user, dispatch } = useContext(AuthContext);

    const handleLoginButton = () => {
        navigate("/login")
      }
      const handleLogoutButton = async (e) => {
        e.preventDefault();
        try {
          const apiUrl = process.env.REACT_APP_API_URL;
          const res = await axios.post(`${apiUrl}/auth/logout`, {}, {withCredentials: true})
          dispatch({ type: "LOGOUT" });
          navigate("/");
          console.log(res.data);
        } catch (err) {
          console.log(err);
        }
    };

    const handleProfileButton = () => {
        if(!user) {
          navigate("/upload");
        } else {
          navigate("/library")
        }
      }

    return (
        <div className="hsearch">
            <form className="searchForm">
                <div className="searchContainer">
                    <div className="searchIcon">
                        <FontAwesomeIcon
                            icon={faMagnifyingGlass}
                            style={{
                                color: "var(--c-dark-gray)",
                                fontSize: "var(--s-medium)",
                            }}
                        />
                    </div>
                    <input
                        className="searchInput"
                        name="searchInput"
                        type="text"
                        placeholder="Search for artists, bands, tracks, podcasts"
                    ></input>
                </div>
            </form>
            <div className="hsearchBtnBox">
                <a className="hsearchBtn" onClick={handleProfileButton}>
                    UPLOAD
                </a>
                <a className="hsearchBtn" href="/library">
                    LIBRARY
                </a>
            </div>
        </div>
    );
};

export default HSearch;
