import useFetch from "../../hooks/useFetch";
import React from "react";

const MusicList = () => {
    const apiUrl = process.env.REACT_APP_API_URL;
    const {
        data: musicData,
        loading,
        error,
    } = useFetch(`${apiUrl}/musics?limit=5`);

    if (loading) {
        return <div>Loading...</div>;
