        <div className="mp">
            {data.map((item, index) => (
                <div className="mpItem" key={index}>
                    <img src={item.thumbimgUrl[0]} alt="" className="mpImg" />
                    <span className="mpTitle">{item.title}</span>
                    <span className="mpUploader">
                        {item.uploader
                            ? item.uploader.username
                            : "Unknown Uploader"}
                    </span>
                    <span>{`조회수 ${item.views}회`}</span>
                </div>
            ))}
        </div>
    );
};

export default MusicList;
