import { BrowserRouter, Routes, Route } from "react-router-dom";
import Home from "./pages/Home";
import Join from "./pages/Join";
import Search from "./pages/Search";
import Upload from "./pages/Upload";
import Music from "./pages/Music";
import UpdateMusic from "./pages/UpdateMusic";
import Login from "./pages/Login";
import Profile from "./pages/Profile";
import { useCookies } from "react-cookie";
import { useEffect, useState } from "react";
import { useRecoilState } from "recoil";
import { cookieState } from "./atoms";
import UpdateProfile from "./pages/UpdateProfile";
import MyProfile from "./pages/MyProfile";

function App() {
    return (
        <BrowserRouter>
            {hasCookie ? <Redirect to="/" /> : <Redirect to="/login" />}
            {/*       서버로부터 받아온 토큰 정보가 쿠키에 저장되어 있지 않다면 LogIn 컴포넌트를 띄우고,
      저장되어 있다면 ToDo 컴포넌트를 띄운다. */}
                <Route path="/upload" element={<Upload />} />
                <Route path="/join" element={<Join />} />
                <Route path="/login" element={<Login />} />
                <Route path="/search" element={<Search />} />
                <Route path="/profile/:id/update" element={<UpdateProfile />} />
                <Route path="/profile/:id" element={<Profile />} />
                <Route path="/myprofile" element={<MyProfile />} />
                <Route path="/:id/update" element={<UpdateMusic />} />
                <Route path="/:id" element={<Music />} />
                <Route path="/" element={<Home />} />
        </BrowserRouter>
    );
}

export default App;
