import axios from "axios";
import { useNavigate } from "react-router-dom";
import { useState } from "react";
import "./auth.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faArrowLeft } from "@fortawesome/free-solid-svg-icons";

const Register = () => {
    const [credentials, setCredentials] = useState({
        username: undefined,
        password: undefined,
        email: undefined,
        phone: undefined,
        country: undefined,
        city: undefined,
    });
    const navigate = useNavigate();

    const handleChange = (e) => {
        setCredentials((prev) => ({ ...prev, [e.target.id]: e.target.value }));
    };

    const handleClick = async (e) => {
        e.preventDefault();
        try {
            const apiUrl = process.env.REACT_APP_API_URL;
            const res = await axios.post(
                `${apiUrl}/auth/register`,
                credentials
            );
            console.log(res.data);
            navigate("/login");
        } catch (err) {
            console.log(err);
        }
    };

    const handleConfirm = async (e) => {
        e.preventDefault();
        try {
        } catch (err) {
            console.log(err);
        }
    };

    const inputStyles = document.querySelectorAll(".button");

    inputStyles.forEach((button) => {
        button.addEventListener("click", function () {
            // 클릭된 요소에 'clicked' 클래스를 추가합니다.
            this.classList.add("clicked");
        });
    });

    return (
        <div className="authForm">
            <a className="backBtn" href="/">
                <FontAwesomeIcon
                    icon={faArrowLeft}
                    color="var(--c-white)"
                    fontSize="var(--s-large)"
                />
            </a>
            <div className="authWrapper">
                <div className="pageTitle">REGISTER</div>
                <form>
                    <label htmlFor="username">아이디</label>
                    <input type="text" id="username" cl onChange={handleChange} />
                    <label htmlFor="password">비밀번호</label>
                    <input
                        type="password"
                        id="password"
                        onChange={handleChange}
                    />
                    <label htmlFor="passwordConfirm">비밀번호 확인</label>
                    <input
                        type="password "
                        id="passwordConfirm"
                        onChange={handleConfirm}
                    />
                    <label htmlFor="phone">전화번호</label>
                    <input type="text" id="phone" onChange={handleChange} />
                    <button
                        type="submit"
                        className="authBtn1"
                        onClick={handleClick}
                    >
                        회원가입
                    </button>
                </form>
            </div>
        </div>
    );
};

export default Register;
