import React, { useState } from "react";
import { useDispatch } from "react-redux";
import { deleteComment } from "../../store/comments";
import EditCommentForm from "./EditCommentForm";

import "./comment.css";

export default function Comment({ comment, user, songId }) {
    const dispatch = useDispatch();
    const [editComForm, setEditComForm] = useState(false);

    return editComForm ? (
        <div className="editForm">
            <EditCommentForm
                musicId={musicId}
                user={user}
                comment={comment}
                setEditComForm={setEditComForm}
            />
        </div>
    ) : (
        <div className="comnentContainer">
            <div className="comnentBox">{comment.user.username}</div>
            <div className="comnents">{comment.comment}</div>
            {user?.id === comment.User.id && (
                <div className="commentBtn">
                    <FontAwesomeIcon
                        icon={faEdit}
                        className="editBtn"
                        onClick={() => setEditComForm(true)}
                    />
                    <FontAwesomeIcon
                        icon={faTrashAlt}
                        className="deleteBtn"
                        onClick={() =>
                            dispatch(
                                deleteComment(musicId, comment.id, user.id)
                            )
                        }
                    />
                </div>
            )}
        </div>
    );
}
