import "./musiclist.css";
import React, { useEffect, useState } from "react";
import useFetch from "../../hooks/useFetch";

const MusicList = () => {
    const [musics, setMusics] = useState([]);

    //뮤직 전체 조회
    const apiUrl = process.env.REACT_APP_API_URL;
    const { data, loading, error } = useEffect(() => {
        fetch(`${apiUrl}/musics`)
            .then((response) => response.json())
            .then((data) => setMusics(data));
    }, []);

    console.log("뮤직url : ", `${apiUrl}/musics`);

    if (loading) {
        console.log("Loading data...");
    } else if (error) {
        console.error("Error fetching data:", error);
    } else {
        console.log("Data from server:", data);
    }

    return (
        <div className="mp">
            {loading ? (
                "loading"
            ) : (
                <>
                    {data.length > 0 &&
                        data.map((music) => (
                            <div className="mpItem" key={music._id}>
                                <img
                                    src={music.thumbimgUrl}
                                    alt=""
                                    className="mpImg"
                                />
                                <span className="mpTitle">{music.title}</span>
                                <span className="mpUploader">
                                    {music.uploader}
                                </span>
                                <span>{`${music.views}회 재생`}</span>
                            </div>
                        ))}
                </>
            )}
        </div>
    );
};

export default MusicList;
