import { Link } from "react-router-dom";
import { useState, useContext, useEffect } from "react";
import { AuthContext } from "../../context/AuthContext";
import axios from "axios";
import useFetch from "../../hooks/useFetch";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUser } from "@fortawesome/free-solid-svg-icons";
import "./profile.css";

const Profile = () => {
    const { user } = useContext(AuthContext);
    const apiUrl = process.env.REACT_APP_API_URL;

    const {
        data: userData,
        loading,
        error,
    } = useFetch(`${apiUrl}/users/${user._id}`);

    const [credentials, setCredentials] = useState({
        profileimg: "",
        password: "",
        username: "",
        phone: "",
        nickname: "",
        bio: "",
    });

    const [imageFile, setImageFile] = useState(null);
    useEffect(() => {
        if (userData) {
            setCredentials(userData);
        }
    }, [userData]);

    console.log("credentials.profileimg : ", credentials.profileimg);

    const handleChange = (e) => {
        setCredentials((prev) => ({ ...prev, [e.target.id]: e.target.value }));
    };

    const handleImageChange = (e) => {
        const file = e.target.files[0];
        setImageFile(file);
        const reader = new FileReader();
        reader.onloadend = () => {
            setCredentials((prev) => ({ ...prev, profileimg: reader.result }));
        };
        reader.readAsDataURL(file);
    };

    if (loading) {
        return <div>loading...</div>;
    }
    if (error) {
        return <div>error : {error.message}</div>;
    }

    const handleClick = async (e) => {
        e.preventDefault();
        try {
            const formData = new FormData();
            formData.append("profileimg", credentials.profileimg);
            formData.append("nickname", credentials.nickname);
            formData.append("password", credentials.password);
            formData.append("phone", credentials.phone);
            formData.append("bio", credentials.bio);
            if (imageFile) {
                formData.append("profile", imageFile);

                console.log("imageFile : ", imageFile);
            }
            const res = await axios.put(
                `${apiUrl}/users/${user._id}`,
                formData,
                {
                    withCredentials: true,
                    headers: {
                        "Content-Type": "multipart/form-data",
                    },
                }
            );
            const newImgUrl = `${apiUrl}/${
                res.data.profileimg
            }?${new Date().getTime()}`;
            setCredentials((prev) => ({ ...prev, profileimg: newImgUrl }));
            console.log("res.data : ", res.data);

            window.location.href = "/profile";
            console.log("newImgUrl : ", newImgUrl);
        } catch (err) {
            console.log(err);
        }
    };

    if (loading) {
        return <div>Loading...</div>;
    }
    if (error) {
        return <div>Error: {error.message}</div>;
    }

    return (
        <div className="profilewrapper">
            <form className="profileForm">
                <div className="pageTitle">MY PAGE</div>
                <div className="ImgeWrapper">
                    <img
                        src={credentials.profileimg}
                        alt="profileimg"
                        className="profileimg"
                        onClick={() =>
                            document.getElementById("imageInput").click()
                        }
                    />
                </div>
                <input
                    type="file"
                    id="imageInput"
                    style={{ display: "none" }}
                    onChange={handleImageChange}
                />
                <label htmlFor="username">아이디</label>
                <input
                    type="text"
                    id="username"
                    autoComplete="off"
                    value={credentials.username}
                    readOnly
                />
                <span className="notChangeable">
                    {" "}
                    아이디는 변경이 불가합니다.{" "}
                </span>
                <br />
                <br />
                <label htmlFor="nickname">닉네임</label>
                <input
                    type="text"
                    id="nickname"
                    autoComplete="off"
                    onChange={handleChange}
                    value={credentials.nickname}
                />
                <label htmlFor="editPassword">비밀번호</label>
                <button
                    type="button"
                    className="editPassword"
                    placeholder="비밀번호 변경하기"
                    onClick={handleChange}
                >
                    비밀번호 변경하기
                </button>

                <div
                    id="editPw"
                    // style={{ display: "none" }}
                    // onChange={handlePwChange}
                >
                    <div className="editPwForm">
                        <div className="editPwFormWrapper">
                            <div className="pageTitle">LOGIN</div>
                            <form>
                                <label htmlFor="CurrentPW">이전 비밀번호</label>
                                <input
                                    type="password"
                                    id="password"
                                    autoComplete="off"
                                    className="CurrentPW"
                                    onChange={handleChange}
                                />
                                {error && alert(error.message)}
                                <label htmlFor="CurrentPW">비밀번호</label>
                                <input
                                    type="password"
                                    id="password"
                                    className="CurrentPW"
                                    autoComplete="off"
                                    // onChange={handleCurrent}
                                />
                                <label htmlFor="CurrentPW">비밀번호 확인</label>
                                <input
                                    type="password"
                                    id="password"
                                    className="CurrentPW"
                                    autoComplete="off"
                                    // onChange={handleCurrent}
                                />
                                <button
                                    type="submit"
                                    className="authBtn1"
                                    disabled={loading}
                                    onClick={handleClick}
                                >
                                    로그인
                                </button>
                                <Link to="/register">
                                    <button type="button" className="authBtn2">
                                        회원가입
                                    </button>
                                </Link>
                            </form>
                            {error && alert(error.message)}
                        </div>
                    </div>
                </div>
                <label htmlFor="phone">전화번호</label>
                <input
                    type="text"
                    id="phone"
                    autoComplete="off"
                    onChange={handleChange}
                    value={credentials.phone}
                />
                <label htmlFor="bio">소개</label>
                <input
                    type="text"
                    id="bio"
                    autoComplete="off"
                    onChange={handleChange}
                    value={credentials.bio}
                />
                <button className="profileBtn" onClick={handleClick}>
                    수정
                </button>
                <Link to="/">
                    <button className="profileBtn" type="button">
                        HOME
                    </button>
                </Link>
            </form>
        </div>
    );
};

export default Profile;
