import axios from "axios";
import { useNavigate } from "react-router-dom";
import { useState } from "react";
import "./auth.css";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSoundcloud } from "@fortawesome/free-brands-svg-icons";
<FontAwesomeIcon icon="faarrowleft" />;

const Register = () => {
    const [credentials, setCredentials] = useState({
        username: undefined,
        password: undefined,
        email: undefined,
        phone: undefined,
        country: undefined,
        city: undefined,
    });
    const navigate = useNavigate();
    const handleChange = (e) => {
        setCredentials((prev) => ({ ...prev, [e.target.id]: e.target.value }));
    };

    const handleClick = async (e) => {
        e.preventDefault();
        try {
            const apiUrl = process.env.REACT_APP_API_URL;
            const res = await axios.post(
                `${apiUrl}/auth/register`,
                credentials
            );
            console.log(res.data);
            navigate("/login");
        } catch (err) {
            console.log(err);
        }
    };

    return (
        <div className="authForm">
            <div className="authWrapper">
                <div className="backButton">
                    <FontAwesomeIcon
                        icon={faArrowleft}
                        color="white"
                        fontSize="60px"
                    />
                </div>
                <div className="pageTitle">REGISTER</div>
                <form>
                    <label htmlFor="username">아이디</label>
                    <input type="text" id="username" onChange={handleChange} />
                    <label htmlFor="password">비밀번호</label>
                    <input
                        type="password"
                        id="password"
                        onChange={handleChange}
                    />
                    <label htmlFor="email">이메일</label>
                    <input type="email" id="email" onChange={handleChange} />
                    <label htmlFor="phone">전화번호</label>
                    <input type="text" id="phone" onChange={handleChange} />
                    <label htmlFor="country">국가</label>
                    <input type="text" id="country" onChange={handleChange} />
                    <label htmlFor="city">주소</label>
                    <input type="text" id="city" onChange={handleChange} />
                    <button type="submit" onClick={handleClick}>
                        회원가입
                    </button>
                </form>
            </div>
        </div>
    );
};

export default Register;
