import "./music.css";
import React, { useState, useContext, useEffect } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
    faHeart,
    faRetweet,
    faShareSquare,
    faBars,
    faEllipsisH,
    faPlay,
} from "@fortawesome/free-solid-svg-icons";
import axios from "axios";
import { AuthContext } from "../../context/AuthContext";
import PageNav from "../../components/nav/PageNav.jsx";
import Footer from "../../components/footer/Footer.jsx";
import Comment from "../../components/comment/Comment.jsx";

const Music = () => {
    const { user } = useContext(AuthContext); // Assuming you have a user context
    const [music, setMusic] = useState(null);
    const [comments, setComments] = useState([]);
    const [isLoaded, setIsLoaded] = useState(false);
    // const musicId = "12345"; // Example music ID

    useEffect(() => {
        // Fetch music data
        axios
            .get(`${process.env.REACT_APP_API_URL}/music/${music._id}`)
            .then((response) => {
                setMusic(response.data);
                setIsLoaded(true);
            })
            .catch((error) => {
                console.error("Error fetching music data:", error);
            });

        // Fetch comments
        axios
            .get(`${process.env.REACT_APP_API_URL}/music/${music._id}/comments`)
            .then((response) => {
                setComments(response.data);
            })
            .catch((error) => {
                console.error("Error fetching comments:", error);
            });
    }, [musicId]);

    if (!isLoaded) {
        return <div>Loading...</div>;
    }

    return (
        <div className="musicWrapper">
            <PageNav />
            <div>
                <div className="musicShow">
                    <div className="musicPlay">
                        <div className="MusicImage">
                            <div className="playerContainer">
                                <audio
                                    className="audioCurrent"
                                    controls
                                    controlsList="nodownload"
                                    src={music?.fileurl}
                                ></audio>
                            </div>
                        </div>
                        <img
                            className="thumbimgUrl"
                            src={music?.thumbimgUrl || "defaultImgPath"}
                            alt="Thumbnail"
                        />
                    </div>
                    <div className="musicInfo">
                        <h2 className="musicTitle">{music?.title}</h2>
                        <h2 className="musicUploader">{music?.uploader}</h2>
                    </div>
                </div>

                <div className="musicComments">
                    <div className="musicCommentsContainer">
                        <div className="musicCommentsBox">
                            <div className="musicCBtnBox">
                                <button className="musicBtn" id="mb1">
                                    <FontAwesomeIcon icon={faHeart} />
                                </button>
                                <button className="musicBtn" id="mb2">
                                    <FontAwesomeIcon icon={faRetweet} />
                                    Repost
                                </button>
                                <button className="musicBtn" id="mb3">
                                    <FontAwesomeIcon icon={faShareSquare} />
                                    Share
                                </button>
                                <button className="musicBtn" id="mb4">
                                    <FontAwesomeIcon icon={faBars} />
                                    Add to Next up
                                </button>
                                <div className="moreDropdown">
                                    <button className="musicBtn" id="mb5">
                                        <FontAwesomeIcon icon={faEllipsisH} />
                                        More
                                    </button>
                                </div>
                            </div>

                            <div className="musicStatsBox">
                                <div className="musicStats">
                                    <FontAwesomeIcon icon={faPlay} />
                                    <p>100</p>
                                </div>
                                <div className="musicStats">
                                    <FontAwesomeIcon icon={faHeart} />
                                    <p>700</p>
                                </div>
                                <div className="musicStats">
                                    <FontAwesomeIcon icon={faRetweet} />
                                    <p>200</p>
                                </div>
                            </div>
                        </div>
                        <div className="CommentsIndex"></div>
                    </div>
                    <div className="DescComments">
                        {comments &&
                            comments.map((comment) => (
                                <Comment
                                    comment={comment}
                                    key={comment.id}
                                    user={user}
                                    musicId={music._id}
                                />
                            ))}
                    </div>
                </div>
            </div>
            <Footer />
        </div>
    );
};

export default Music;
