import "./musiclist.css";
import useFetch from "../../hooks/useFetch";
import React, { useEffect, useState } from "react";

const MusicList = () => {
    const [musics, setMusics] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const apiUrl = process.env.REACT_APP_API_URL;
                const response = useFetch(`${apiUrl}/musics`);
                if (!response.ok) {
                    throw new Error("Network response was not ok");
                }
                const data = await response.json();
                setMusics(data);
            } catch (err) {
                setError(err);
            } finally {
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error fetching data: {error.message}</div>;
    }

    return (
        <div className="mp">
            {musics.map((music, index) => (
                <div className="mpItem" key={index}>
                    <img
                        src={music.thumbimgUrl}
                        alt={music.title}
                        className="mpImg"
                    />
                    <span className="mpTitle">{music.title}</span>
                    <span className="mpUploader">
                        {music.uploader
                            ? music.uploader.username
                            : "Unknown Uploader"}
                    </span>
                    <span>{`${music.views}회 재생`}</span>
                </div>
            ))}
        </div>
    );
};

export default MusicList;
