import { Link } from "react-router-dom";
import { useState, useContext, useEffect } from "react";
import { AuthContext } from "../../context/AuthContext";
import axios from "axios";
import useFetch from "../../hooks/useFetch";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faUser } from "@fortawesome/free-solid-svg-icons";
import "./profile.css";

const Profile = () => {
    const { user } = useContext(AuthContext);
    const apiUrl = process.env.REACT_APP_API_URL;

    const {
        data: userData,
        loading,
        error,
    } = useFetch(`${apiUrl}/users/${user._id}`);

    const [credentials, setCredentials] = useState({
        img: "",
        username: "",
        phone: "",
        nickname: "",
        bio: "",
    });

    const [imageFile, setImageFile] = useState(null);
    useEffect(() => {
        if (userData) {
            setCredentials(userData);
        }
    }, [userData]);

    // const ProfileImage = () => {
    //     {
    //         src ? (
    //             <img src="#" alt="#" onClick={onClick} className="profileimg" />
    //         ) : (
    //             <div onClick={onClick} className="profileimg">
    //                 <FontAwesomeIcon
    //                     icon={faUser}
    //                     style={{
    //                         color: "var(--c-white)",
    //                         fontSize: "86px",
    //                     }}
    //                 />
    //             </div>
    //         );
    //     }
    // };

    // useEffect(() => {
    //     if (userData) {
    //         setCredentials({
    //             phone: userData.phone || "",
    //             nickname: userData.nickname || "",
    //             img: userData.img || "",
    //             bio: userData.bio || "",
    //             username: userData.username || "",
    //         });
    //     }
    // }, [userData]);

    console.log(credentials.img);

    const handleChange = (e) => {
        setCredentials((prev) => ({ ...prev, [e.target.id]: e.target.value }));
    };

    const handleImageChange = (e) => {
        const file = e.target.files[0];
        setImageFile(file);
        const reader = new FileReader();
        reader.onloadend = () => {
            setCredentials((prev) => ({ ...prev, img: reader.result }));
        };
        reader.readAsDataURL(file);
    };

    if (loading) {
        return <div>loading...</div>;
    }
    if (error) {
        return <div>error : {error.message}</div>;
    }

    const handleClick = async (e) => {
        e.preventDefault();
        try {
            const formData = new FormData();
            formData.append("phone", credentials.phone);
            formData.append("nickname", credentials.nickname);
            formData.append("img", credentials.img);
            formData.append("bio", credentials.bio);
            if (imageFile) {
                formData.append("profile", imageFile);
                console.log(imageFile);
            }
            const res = await axios.put(
                `${apiUrl}/users/${user._id}`,
                formData,
                {
                    withCredentials: true,
                    headers: {
                        "Content-Type": "multipart/form-data",
                    },
                }
            );
            const newImgUrl = `${apiUrl}/${
                res.data.img
            }?${new Date().getTime()}`;
            setCredentials((prev) => ({ ...prev, img: newImgUrl }));
            console.log(res.data);
            window.location.href = "/profile";
            console.log("newImgUrl : ", newImgUrl);
        } catch (err) {
            console.log(err);
        }
    };

    if (loading) {
        return <div>Loading...</div>;
    }
    if (error) {
        return <div>Error: {error.message}</div>;
    }

    return (
        <div className="profilewrapper">
            <form className="profileForm">
                <div className="pageTitle">MY PAGE</div>
                <div className="ImgeWrapper">
                    <div className="ImageContainer">
                        <ProfileImage
                            className="profileimage"
                            onClick={() =>
                                document.getElementById("imageInput").click()
                            }
                        />
                    </div>
                </div>
                <input
                    type="file"
                    id="imageInput"
                    style={{ display: "none" }}
                    onChange={handleImageChange}
                />
                <label htmlFor="username">아이디</label>
                <input
                    type="text"
                    id="username"
                    autoComplete="off"
                    value={credentials.username}
                    readOnly
                />
                <span className="notChangeable">
                    {" "}
                    아이디는 변경이 불가합니다.{" "}
                </span>
                <br />
                <br />
                <label htmlFor="nickname">닉네임</label>
                <input
                    type="text"
                    id="nickname"
                    autoComplete="off"
                    onChange={handleChange}
                    value={credentials.nickname}
                />
                <label htmlFor="phone">전화번호</label>
                <input
                    type="text"
                    id="phone"
                    autoComplete="off"
                    onChange={handleChange}
                    value={credentials.phone}
                />
                <label htmlFor="bio">소개</label>
                <input
                    type="text"
                    id="bio"
                    autoComplete="off"
                    onChange={handleChange}
                    value={credentials.bio}
                />
                <button onClick={handleClick}>수정</button>
                <Link to="/">
                    <button>확인</button>
                </Link>
            </form>
        </div>
    );
};

export default Profile;
