import { Link } from "react-router-dom";
import { useState, useContext, useEffect } from "react";
import { AuthContext } from "../../context/AuthContext";
import axios from "axios";
import useFetch from "../../hooks/useFetch";
import "./profile.css";

const Profile = () => {
    // user 로그인 정보 가져오기
    const { user } = useContext(AuthContext);
    const apiUrl = process.env.REACT_APP_API_URL;

    // user 정보 가져오기
    const {
        data: userData,
        loading,
        error,
    } = useFetch(`${apiUrl}/users/${user._id}`);

    // user 정보 저장
    const [credentials, setCredentials] = useState({
        phone: '',
        nickname: '',
        img: '',
        bio: '',
        username: '', // 추가
    });

    // 업로드한 프로필 이미지 저장
    const [imageFile, setImageFile] = useState(null);

    // user 정보를 userData에서 받아와서 credentials에 저장
    useEffect(() => {
        if (userData) {
            setCredentials((prev) => ({
                ...prev,
                phone: userData.phone || '',
                nickname: userData.nickname || '',
                img: userData.img || '',
                bio: userData.bio || '',
                username: userData.username || '', // 추가
            }));
        }
    }, [userData]);

    console.log("credentials.img : ", credentials.img);

    // 입력 정보 credentials에 저장
    const handleChange = (e) => {
        setCredentials((prev) => ({ ...prev, [e.target.id]: e.target.value }));
    };

    // 프로필 이미지 업로드 호출 함수
    const handleImageChange = (e) => {
        const file = e.target.files[0];
        setImageFile(file);
        const reader = new FileReader();
        reader.onloadend = () => {
            setCredentials((prev) => ({ ...prev, img: reader.result }));
        };
        reader.readAsDataURL(file);
    };

    // 데이터 불러오는 함수
    if (loading) {
        return <div>loading...</div>;
    }
    if (error) {
        return <div>error : {error.message}</div>;
    }

    // 수정 완료 후 입력 정보 제출 시 호출 함수
    const handleClick = async (e) => {
        e.preventDefault();
        try {
            const formData = new FormData();
            formData.append("phone", credentials.phone);
            formData.append("nickname", credentials.nickname);
            formData.append("img", credentials.img);
            formData.append("bio", credentials.bio);
            if (imageFile) {
                formData.append("profile", imageFile);
            }
            const res = await axios.put(
                `${apiUrl}/users/${user._id}`,
                formData,
                {
                    withCredentials: true,
                    headers: {
                        "Content-Type": "multipart/form-data",
                    },
                }
            );
            const newImgUrl = `${apiUrl}/${
                res.data.img
            }?${new Date().getTime()}`;
            setCredentials((prev) => ({ ...prev, img: newImgUrl }));
            console.log(res.data);
            window.location.href = "/profile";
        } catch (err) {
            console.log(err);
        }
    };

    return (
        <div className="authForm">
            <form>
                <img
                    src={credentials.img}
                    alt="200 x 200 size"
                    className="profileimg"
                    onClick={() =>
                        document.getElementById("imageInput").click()
                    }
                />
                <input
                    type="file"
                    id="imageInput"
                    style={{ display: "none" }}
                    onChange={handleImageChange}
                />
                <label htmlFor="username">아이디</label>
                <input
                    type="text"
                    id="username"
                    autoComplete="off"
                    value={credentials.username}
                    readOnly
                />
                <label htmlFor="nickname">닉네임</label>
                <input
                    type="text"
                    id="nickname"
                    autoComplete="off"
                    onChange={handleChange}
                    value={credentials.nickname}
                />
                <label htmlFor="phone">전화번호</label>
                <input
                    type="text"
                    id="phone"
                    autoComplete="off"
                    onChange={handleChange}
                    value={credentials.phone}
                />
                <label htmlFor="bio">소개</label>
                <input
                    type="text"
                    id="bio"
                    autoComplete="off"
                    onChange={handleChange}
                    value={credentials.bio}
                />
                <button onClick={handleClick}>수정</button>
                <Link to="/">
                    <button>확인</button>
                </Link>
            </form>
        </div>
    );
};

export default Profile;
