import { Link } from "react-router-dom";
import { useState, useContext, useEffect } from "react";
import { AuthContext } from "../../context/AuthContext";
import axios from 'axios';
import useFetch from "../../hooks/useFetch";
import "./footer.css";

const MyPage = () => {
    const { user } = useContext(AuthContext);
    const apiUrl = process.env.REACT_APP_API_URL;
    const { data: userData, loading, error } = useFetch(`${apiUrl}/users/${user._id}`);
    const [credentials, setCredentials] = useState({
        username: "",
        email: "",
        phone: "",
        country: "",
        city: "",
        img: "",
    });
    const [imageFile, setImageFile] = useState(null);
    useEffect(() => {
        if (userData) {
            setCredentials(userData);
        }
    }, [userData]);
    // useEffect(() => {
    //     const fetchData = async () => {
    //         try {
    //             const res = await axios.get(
    //                 `${apiUrl}/users/${user._id}`,
    //                 { withCredentials: true }
    //             );
    //             setCredentials(res.data);
    //         } catch (err) {
    //             console.log(err);
    //         }
    //     };
    //     fetchData();
    // }, [apiUrl, user._id]);

    console.log(credentials.img);
    const handleChange = (e) => {
        setCredentials((prev) => ({ ...prev, [e.target.id]: e.target.value }));
    };

    const handleImageChange = (e) => {
        const file = e.target.files[0];
        setImageFile(file);
        const reader = new FileReader();
        reader.onloadend = () => {
            setCredentials((prev) => ({ ...prev, img: reader.result }));
        };
        reader.readAsDataURL(file);
    }
    // useEffect(() => {
    //     console.log(credentials.img);
    // }, [credentials.img]);
    if(loading){
        return <div>loading...</div>
    }
    if (error) {
        return <div>error : {error.message}</div>;
    }

    const handleClick = async (e) => {
        e.preventDefault();
        try {
            const formData = new FormData();
            formData.append("username", credentials.username);
            formData.append("email", credentials.email);
            formData.append("phone", credentials.phone);
            formData.append("country", credentials.country);
            formData.append("city", credentials.city);
            if(imageFile){
                formData.append("profile", imageFile);
                //console.log(imageFile);
            }
            const res = await axios.put(
                `${apiUrl}/users/${user._id}`,
                formData,
                { withCredentials: true,
                    headers: {
                        "Content-Type": "multipart/form-data"
                    }
                }
            );
            const newImgUrl = `${apiUrl}/${res.data.img}?${new Date().getTime()}`;
            setCredentials((prev) => ({ ...prev, img: newImgUrl }));
            console.log(res.data);
            window.location.href = '/profile';
        } catch (err) {
            console.log(err);
        }
    };


    return (
        <div className="auth-form">
            <form>
                <img src={credentials.img} alt="200 x 200 size" className="profile" onClick={() => document.getElementById('imageInput').click()}/>
                <input type="file" id="imageInput" style={{ display: 'none' }} onChange={handleImageChange} />
                <label htmlFor="username">아이디</label>
                <input type="text" id="username" onChange={handleChange} value={credentials.username} />
                <label htmlFor="email">이메일</label>
                <input type="text" id="email" onChange={handleChange} value={credentials.email} />
                <label htmlFor="phone">전화번호</label>
                <input type="text" id="phone" onChange={handleChange} value={credentials.phone} />
                <label htmlFor="country">나라</label>
                <input type="text" id="country" onChange={handleChange} value={credentials.country} />
                <label htmlFor="city">주소</label>
                <input type="text" id="city" onChange={handleChange} value={credentials.city} />
                <button onClick={handleClick}>수정</button>
                <Link to="/">
                    <button>확인</button>
                </Link>
            </form>
        </div>
    );
};

export default MyPage;
